/*Table structure for table `leave_msg` */

DROP TABLE IF EXISTS `leave_msg`;

CREATE TABLE `leave_msg` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `name` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '留言人',
  `content` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '留言内容',
  `time` date DEFAULT NULL COMMENT '留言时间',
