/*Table structure for table `userinfo` */

DROP TABLE IF EXISTS `userinfo`;

CREATE TABLE `userinfo` (
  `id` int(4) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `login_id` int(4) DEFAULT NULL COMMENT '管理员ID',
  `real_name` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '姓名',
  `email` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '邮箱',
  `regist_time` date DEFAULT NULL COMMENT '注册时间',
  `last_login_time` date DEFAULT NULL COMMENT '最后登录时间',
  PRIMARY KEY (`id`),
  KEY `loginid` (`login_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3706 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

